sys_script_include.script:
var AskGenAIUtils = Class.create();
AskGenAIUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    helpUserCheckIfValidIncident: function() {
        var incCategory = this.getParameter('category');
        var incSubcategory = this.getParameter('subcategory');
        var incShortDescription = this.getParameter('short_description');
        var incDescription = this.getParameter('description');

        var content = "I am about to create an Incident in ServiceNow with the following details: ";
        if (incCategory) {
            content += "Category: " + incCategory + ", ";
        }
        if (incSubcategory) {
            content += "Subcategory: " + incSubcategory + ", ";
        }
        if (incShortDescription) {
            content += "Short description: " + incShortDescription + ", ";
        }
        if (incDescription) {
            content += "Description: " + incDescription + ". ";
        }
        content += "Please help me in checking that this is a valid issue first, before creating the Incident. If possible, help me to resolve this issue. Your response should only consist of the points which I need to check, nothing extra.";
        var answer = this.getResponse(content);
        if (answer) {
            return answer;
        } else {
            return "I didn't understand your question. Please rephrase it for me once again.";
        }
    },

    getResponse: function(content) {

        var answer = undefined;

        try {

            var restMessage = new sn_ws.RESTMessageV2('OpenAI', 'OpenAI - POST');
            restMessage.setStringParameterNoEscape('model', "gpt-3.5-turbo");
            restMessage.setStringParameterNoEscape('content', content);
            restMessage.setStringParameterNoEscape('max_tokens', 400);
            restMessage.setStringParameterNoEscape('temperature', 0);

            var response = restMessage.execute();
            var responseBody = response.getBody();
            var statusCode = response.getStatusCode();

            var responseObj = JSON.parse(responseBody);
            // current.u_request = r.getRequestBody(); //it will give the request body
            // current.u_response = responseBody;
            // current.u_status_code = code;
            if (statusCode == 200) {
                answer = responseObj.choices[0].message.content;
            }

        } catch (ex) {
            var message = ex.message;
            gs.info("OpenAI REST API Error: " + message);
        }

        return answer;
    },

    type: 'AskGenAIUtils'
});
